import React, { useEffect, useState } from "react";
import defaultBasicInfo from "../../../../DefaultValues/DefaultBasicInfo";
const PokemonStats = (props) => {
  const [stats, setStats] = useState(defaultBasicInfo);

  useEffect(() => {
    const getStats = async () => {
      try {
        let api = await props.stats;
        console.log(api);
        console.log(api.stats);
        let stat = {
          hp: api.stats[0].base_stat,
          attack: api[1].base_stat,
          defence: api[2].base_stat,
          special_attack: api[3].base_stat,
          special_defence: api[4].base_stat,
          speed: api[5].base_stat,
        };
        
      } catch {}
    };
    getStats();
  }, []);
  return <div>Pokemon Stats</div>;
};

export default PokemonStats;
