import { useState } from 'react';
import PokemonApi from './api/PokemonAPI'
function App() {
  const [showList,setShowList] = useState(false)
  const showListHandler = () => {
    setShowList(true)
  }
  const defaultView = () => {
    return (
      <div>
        <h2>Pokedex</h2>
      </div>
    )
  }
  return (
    <div>
      <h2>Pokemon Pokedex</h2>
      <div>
        <button onClick = {showListHandler}></button>
      </div>
      <PokemonApi/>
    </div>
  );
}

export default App;
