import React, { useState, useEffect } from "react";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import styles from "./PokemonFullStatus.module.css";
import PokemonSummary from "./Stats/PokemonSummary/PokemonSummary";
const PokemonFullStatus = (props) => {
  let summaries = props.speciesData.flavor_text_entries.map((summary) => {
    return summary.flavor_text;
  });
  console.log(summaries)
  console.log(props.speciesData);
  return (
    <div className={styles.mainFullStatus}>
      <Tabs>
        <TabList>
          <Tab>Summary</Tab>
          <Tab>Basic Information</Tab>
          <Tab>Stats</Tab>
          <Tab>Attributes</Tab>
          <Tab>Moves</Tab>
          <Tab>Location</Tab>
        </TabList>
        <TabPanel>
          <h2>Summary</h2>
          <PokemonSummary />
        </TabPanel>
        <TabPanel>
          <h2>Basic Information</h2>
        </TabPanel>
        <TabPanel>
          <h2>Stats</h2>
        </TabPanel>
        <TabPanel>
          <h2>Attributes</h2>
        </TabPanel>
        <TabPanel>
          <h2>Moves</h2>
        </TabPanel>
        <TabPanel>
          <h2>Locations</h2>
        </TabPanel>
      </Tabs>
    </div>
  );
};
export default PokemonFullStatus;
