import React, { useState } from "react";
import PokemonPreview from "../PokemonPreview/PokemonPreview";
const defaultPreview = {
  name: "Bulbasaur",
  id: 1,
};
const PokemonList = (props) => {
  const [previewData, setPreviewData] = useState(defaultPreview);
  const previewHandler = (id, name) => {
    setPreviewData({ id: id, name: name });
  };
  const pokemonList = props.pokemonData.map((pokemon) => {
    return (
      <li
        key={pokemon.id}
        onClick={() => previewHandler(pokemon.id, pokemon.name,pokemon.sprites.other['offical'])}
      >
        <div>{pokemon.id}</div>
        <div>
          <img src={pokemon.sprites.front_default} alt="pokemon sprite" />
        </div>
        <div>{pokemon.name}</div>
      </li>
    );
  });

  return (
    <React.Fragment>
      <div>
        <PokemonPreview previewInfo={previewData} />
      </div>
      <div>
        <ul>
          <div>{pokemonList}</div>
        </ul>
      </div>
    </React.Fragment>
  );
};
export default PokemonList;
