import React, { useState, useEffect } from "react";
import styles from "./PokemonFullStatus.module.css";
const PokemonFullStatus = (props) => {
  const [api, setApi] = useState([]);
  useEffect(() => {
    fetchApi();
  }, []);

  const fetchApi = async () => {
    try {
      console.log(props.fullStatus.species.url);
      let response = await fetch(`https://pokeapi.co/api/v2/pokemon-species/${props.pokemon.id}/`);
      let data = await response.json();
      setApi(data);
    } catch {
      console.error("Unable to fetch Api");
    }
  };
  console.log(props.fullStatus);
  console.log(api);
  return (
    <div className={styles.mainFullStatus}>
      <h3>Status loaded</h3>
    </div>
  );
};
export default PokemonFullStatus;
