  //       });
  //       setStats(baseStat);
  //     } catch {}
  //   };
  //   getStats();
  // }, []);
  const baseStats = props.stats.map((stat)=>{
    return stat.base_stat
  })
  set

  const listItems = (name, stat) => {
    return (
      <div>
        <li>
          <div>{name}</div>
          <div>{stat}</div>
        </li>
      </div>
    );
  };
  return (
    <TabCard>
      <div>Pokemon Stats</div>
      <div>
        <ul>
          {listItems("HP", stats[0])}
          {listItems("Attack", stats[1])}
          {listItems("Defence", stats[2])}
        </ul>
      </div>
      <div></div>
    </TabCard>
  );
};

export default PokemonStats;
